import React, { useContext } from 'react'
import GoogleLogin from 'react-google-login'
import axios from 'axios'

import { AuthContext } from '../context/auth'

const Login = () => {
  const { loginUser } = useContext(AuthContext)

  const responseSuccessGoogle = (response: any) => {
    axios({
      method: 'POST',
      url: 'http://localhost:5000/api/auth/login/google',
      data: { idToken: response.tokenId },
    }).then((response) => {
      //console.log('response', response)
      loginUser(response.data.payload.token)
    })
  }

  const responseFailureGoogle = (res: any) => {
    console.log('error')
  }

  return (
    <>
      <div className='google-login-div'>
      <GoogleLogin
        clientId='49262481225-ntb03gjepdqs7ocerrm96g8iclg6pbm0.apps.googleusercontent.com'
        buttonText='Login with Google'
        onSuccess={responseSuccessGoogle}
        onFailure={responseFailureGoogle}
        cookiePolicy={'single_host_origin'}
      />
      </div>
      <div className='signup-div'>
      <button
        type='button'
        style={{ 
          backgroundColor: 'white', 
          display: 'inlineFlex', 
          alignItems: 'center', 
          color: 'rgba(0, 0, 0, 0.54)', 
          boxShadow: 'rgba(0, 0, 0, 0.24) 0px 2px 2px 0px, rgba(0, 0, 0, 0.24) 0px 0px 1px 0px', 
          padding: '0px', 
          borderRadius: '2px', 
          border: '1px solid transparent', 
          fontSize: '14px', 
          fontWeight: '500', 
          fontFamily: 'Roboto, sans-serif' 
        }}
      >
        <div
          style={{
            marginRight: '10px',
            background: 'rgb(255, 255, 255)',
            padding: '10px',
            borderRadius: '2px',
          }}
        >
        </div>
        <span style={{ padding: '10px 10px 10px 0px', fontWeight: '500' }}>
          Login with Google
        </span>
      </button>
      </div>
    </>
  )
}

export default Login
